from django.core import validatorsfrom django.db import modelsfrom django.contrib.auth.models import AbstractBaseUser, UserManager, PermissionsMixinfrom django.utils import timezonefrom django.utils.translation import ugettext_lazy as _from ckeditor.fields import RichTextFieldimport reclass Staff(AbstractBaseUser, PermissionsMixin):    username = models.CharField(_('username'), max_length=30, unique=True,        help_text=_('Required. 30 characters or fewer. Letters, numbers and '                    '@/./+/-/_ characters'),        validators=[            validators.RegexValidator(re.compile('^[\w.@+-]+$'), _('Enter a valid username.'), 'invalid')        ])    email = models.EmailField(        verbose_name=_('email address'),        max_length=255,        unique=True,    )    first_name = models.CharField(_('first name'), max_length=30, null=True, blank=True)    last_name = models.CharField(_('last name'), max_length=30, null=True, blank=True)    occupation = models.CharField(max_length=255, null=True, blank=True)    description = RichTextField(null=True, blank=True)    homepage_url = models.CharField(max_length=255, null=True, blank=True)    is_staff = models.BooleanField(_('staff status'), default=False,        help_text=_('Designates whether the user can log into this admin '                    'site.'))    is_active = models.BooleanField(_('active'), default=True,        help_text=_('Designates whether this user should be treated as '                    'active. Unselect this instead of deleting accounts.'))    date_joined = models.DateTimeField(_('date joined'), default=timezone.now)    objects = UserManager()    USERNAME_FIELD = 'username'    REQUIRED_FIELDS = ['email']    def get_full_name(self):        """        Returns the first_name plus the last_name, with a space in between.        """        full_name = '%s %s' % (self.first_name, self.last_name)        return full_name.strip()    def get_short_name(self):        "Returns the short name for the user."        if self.first_name.strip() and self.last_name.strip():            return '%s.%s' % (self.first_name.strip(), self.last_name.strip()[0])        elif self.first_name.strip():            return self.first_name.strip()        elif self.last_name.strip():            return self.last_name.strip()        return '';class People(AbstractBaseUser):    username = models.CharField(_('username'), max_length=30, unique=True,        help_text=_('Required. 30 characters or fewer. Letters, numbers and '                    '@/./+/-/_ characters'),        validators=[            validators.RegexValidator(re.compile('^[\w.@+-]+$'), _('Enter a valid username.'), 'invalid')        ])    first_name = models.CharField(_('first name'), max_length=30, null=True, blank=True)    last_name = models.CharField(_('last name'), max_length=30, null=True, blank=True)    occupation = models.CharField(max_length=255, null=True, blank=True)    description = RichTextField(null=True, blank=True)    homepage_url = models.CharField(max_length=255, null=True, blank=True)    def get_full_name(self):        """        Returns the first_name plus the last_name, with a space in between.        """        full_name = '%s %s' % (self.first_name, self.last_name)        return full_name.strip()    def get_short_name(self):        "Returns the short name for the user."        if self.first_name.strip() and self.last_name.strip():            return '%s.%s' % (self.first_name.strip(), self.last_name.strip()[0])        elif self.first_name.strip():            return self.first_name.strip()        elif self.last_name.strip():            return self.last_name.strip()        return '';